import { Component, OnInit } from '@angular/core';
import { FoodtruckService } from '../services/foodtruck/foodtruck.service';
import { tap } from 'rxjs/operators';
import { Router } from '@angular/router';

@Component({
  selector: 'app-accueil',
  templateUrl: './accueil.component.html',
  styleUrls: ['./accueil.component.css']
})
export class AccueilComponent implements OnInit {

  foodtrucks: any = [];

  constructor(private foodtruckService: FoodtruckService,private router: Router) { }

  ngOnInit(): void {
    this.loadFoodtrucks();
  }

  loadFoodtrucks(): void {
    this.foodtruckService.getAllFoodtrucks().pipe(
      tap((response: any) => {
        const data = response.data; // Récupérer le token depuis la réponse
        const user=response.user
        localStorage.setItem('foodtrucks', data); // Stocker le token dans le stockage local
        console.log(localStorage.getItem('foodtrucks'))
      })
    )
    .subscribe({
      next: (data) => data,
      error: (error) => error,
      complete: () => console.log('complete')
    });
    this.router.navigate(['/']);
  }

}
